import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [bookings, setBookings] = useState([]);
  const [sortBy, setSortBy] = useState('checkin');
  const [order, setOrder] = useState('asc');

  useEffect(() => {
    // Fetch bookings from the Django API
    axios
      .get(`http://127.0.0.1:8000/api/bookings-table/?sort_by=${sortBy}&order=${order}`)
      .then(response => {
        setBookings(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the bookings!', error);
      });
  }, [sortBy, order]);

  const handleSortChange = (field) => {
    if (sortBy === field) {
      setOrder(order === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setOrder('asc');
    }
  };

  return (
    <div className="App">
      <h1>Bookings Table</h1>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSortChange('flat__name')}>Flat Name</th>
            <th>ID</th>
            <th onClick={() => handleSortChange('checkin')}>Check-in</th>
            <th onClick={() => handleSortChange('checkout')}>Check-out</th>
            <th>Previous Booking ID</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking.id}>
              <td>{booking.flat.name}</td>
              <td>{booking.id}</td>
              <td>{booking.checkin}</td>
              <td>{booking.checkout}</td>
              <td>{booking.previous_booking_id || '-'}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
