import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, Container, Spinner, Alert } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  const [bookings, setBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [sortBy, setSortBy] = useState('flat__name');
  const [sortOrder, setSortOrder] = useState('asc');

  useEffect(() => {
    axios
      .get(`http://127.0.0.1:8000/api/bookings-table/?sort_by=${sortBy}&order=${sortOrder}`)
      .then((response) => {
        setBookings(response.data);
        setIsLoading(false);
      })
      .catch((err) => {
        setError(err);
        setIsLoading(false);
      });
  }, [sortBy, sortOrder]);

  const handleSortChange = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(column);
      setSortOrder('asc');
    }
  };

  const getSortIndicator = (column) => {
    if (sortBy === column) {
      return sortOrder === 'asc' ? ' ↑' : ' ↓';
    }
    return '';
  };

  if (isLoading) {
    return (
      <Container className="text-center">
        <Spinner animation="border" role="status">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="text-center">
        <Alert variant="danger">There was an error fetching the bookings. Please try again later.</Alert>
      </Container>
    );
  }

  return (
    <Container>
      <h2 className="my-4 text-center">Bookings Overview</h2>
      <Table striped bordered hover responsive className="my-4">
        <thead>
          <tr>
            <th onClick={() => handleSortChange('flat__name')} style={{ cursor: 'pointer' }}>
              Flat Name {getSortIndicator('flat__name')}
            </th>
            <th>ID</th>
            <th onClick={() => handleSortChange('checkin')} style={{ cursor: 'pointer' }}>
              Check-in {getSortIndicator('checkin')}
            </th>
            <th onClick={() => handleSortChange('checkout')} style={{ cursor: 'pointer' }}>
              Check-out {getSortIndicator('checkout')}
            </th>
            <th>Previous Booking ID</th>
          </tr>
        </thead>
        <tbody>
          {bookings.map((booking) => (
            <tr key={booking.id}>
              <td>{booking.flat.name}</td>
              <td>{booking.id}</td>
              <td>{booking.checkin}</td>
              <td>{booking.checkout}</td>
              <td>{booking.previous_booking_id || '-'}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </Container>
  );
}

export default App;
